    public function recoverSelected($json,$name,$i,$em){
        $decoded = json_decode($json);
        foreach($decoded as $item){
            if($item->article == "ZZ/27"){
                $bb = 0;
            }
            $result = $i->getProductsByCategoryRecovery($name,trim($item->article));
